  def han
    text = quote
    @image = picture
    @lang = language params[:language]

    @dk_quote = DANSKE_CITERE[text]
    @sv_quote = SVENSKA_CITERE[text]

    if @lang == 'dansk'
      @quote = i_dansk text
      @tr_quote = @dk_quote
      @alang = 'svenska'
    elsif @lang == 'svenska'    
      @quote = i_svenska text
      @tr_quote = @sv_quote
      @alang = 'dansk'
    end
  end

  private

  # Bing Cognitive client
  def bing
    @bing ||= CognitiveBing.new(ENV['BING_KEY'])
  end

  def quote
    DANSKE_CITERE.keys[Random.rand(DANSKE_CITERE.size - 1)]
  end

  def picture
    ps = pictures.size - 1
    ps = 1 if ps < 1
    i = Random.rand(ps)
    pictures[i].keys.first
  end

  def pictures
    fetch_images
  end

  # expensive API call so use sparingly
  # get 200 images at a time -- gem or API wont' allow more than 50 per call
  def fetch_images
    Link.all.map do |link|
      {link.url => link.thumbnail_url}
    end
  end

  # Convert all 'a' and 'o' to 'å' and 'ø' preserving case
  # one third of the time (theoretically)
  # Yes I know Danish doesn't work this way
  # Tilgiv mig
  def i_dansk(text)
    x = /[aoAO]/
    r = { 'a' => 'å', 'A' => 'Å', 'o' => 'ø', 'O' => 'Ø' }
    likelihood = 9
    udtryk = ''
    text.each_char do |l|
      rep = Random.rand(likelihood) == 2
      likelihood += 3 if rep
      udtryk += rep ? l.gsub(x, r) : l
    end
    udtryk
  end

  def i_svenska(text)
    i_dansk text
  end

